import React, { useState } from 'react';
import { getParticipants } from '../api';

import Autocomplete from '@material-ui/lab/Autocomplete';
import { makeStyles, TextField, Button, Container } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
	input: {
		width: '100%',
		'& > * + *': {
			marginTop: theme.spacing(2)
		}
	},
	timeField: {
		marginLeft: theme.spacing(1),
		marginRight: theme.spacing(1),
		width: 200
	},
	textField: {
		width: '100%'
	}
}));

export default function NewInterview() {
	const classes = useStyles();
	const [ name, setName ] = useState();
	const [ description, setDescription ] = useState();
	const [ candOpt, setCandOpt ] = useState([]);
	const [ candidates, setCandidates ] = useState();
	const [ interOpt, setInterOpt ] = useState([]);
	const [ interviewers, setInterviewers ] = useState();
	const [ date, setDate ] = useState();
	const [ startTime, setStartTime ] = useState();
	const [ endTime, setEndTime ] = useState();

	async function updateCandOpts(event, value, reason) {
		if (value.length !== 1) return;
		setCandOpt(await getParticipants({ query: value.toLowerCase(), role: 1 }));
	}

	async function updateInterOpts(event, value, reason) {
		if (value.length !== 1) return;
		setInterOpt(await getParticipants({ query: value.toLowerCase(), role: 0 }));
	}

	const nameField = (
		<TextField
			id="name"
			label="Name"
			className={classes.textField}
			onChange={(event) => setName(event.target.value)}
		/>
	);

	const descriptionField = (
		<TextField
			id="description"
			label="Description"
			className={classes.textField}
			multiline
			rows={4}
			onChange={(event) => setDescription(event.target.value)}
		/>
	);

	const participantField = (
		<Autocomplete
			multiple
			filterSelectedOptions
			id="candidates"
			size="small"
			options={candOpt}
			getOptionLabel={(option) => option.name}
			getOptionSelected={(option, value) => option.id === value.id}
			onInputChange={updateCandOpts}
			onChange={(evt, value) => setCandidates(value)}
			renderInput={(params) => (
				<TextField {...params} variant="standard" label="Select Candidates" placeholder="candidates" />
			)}
		/>
	);

	const interviewerField = (
		<Autocomplete
			multiple
			filterSelectedOptions
			id="interviewer"
			size="small"
			options={interOpt}
			getOptionLabel={(option) => option.name}
			getOptionSelected={(option, value) => option.id === value.id}
			onInputChange={updateInterOpts}
			onChange={(evt, value) => setInterviewers(value)}
			renderInput={(params) => (
				<TextField {...params} variant="standard" label="Select Interviewers" placeholder="Interviewers" />
			)}
		/>
	);

	const dateField = (
		<TextField
			id="date"
			label="Date"
			type="date"
			className={classes.timeField}
			InputLabelProps={{
				shrink: true
			}}
			onChange={(event) => setDate(event.target.value)}
		/>
	);

	const startTimeField = (
		<TextField
			id="start-time"
			label="Start Time"
			type="time"
			className={classes.timeField}
			InputLabelProps={{
				shrink: true
			}}
			onChange={(event) => setStartTime(event.target.value)}
		/>
	);

	const endTimeField = (
		<TextField
			id="end-time"
			label="End Time"
			type="time"
			className={classes.timeField}
			InputLabelProps={{
				shrink: true
			}}
			onChange={(event) => setEndTime(event.target.value)}
		/>
	);

	function formSubmit(event) {
		// use start_time.toISOString() to convert to the required format
		const start_time = new Date(date + ' ' + startTime);
		const end_time = new Date(date + ' ' + endTime);
		// rest of the params:
		// date
		// candidates
		// interviewers
		// name
		// description
		// do validation here

		if (candidates.length && interviewers.length && start_time.getTime() < end_time.getTime()) {
			const details = {
				name,
				description,
				date,
				start_time,
				end_time,
				interviewers,
				candidates
			};
		}
		console.log(name);
		console.log(description);
		console.log(date);
		console.log(start_time);
		console.log(end_time);
		console.log(candidates);
		console.log(interviewers);
	}
	return (
		<Container>
			<form className={classes.input}>
				{nameField}
				{descriptionField}
				{participantField}
				{interviewerField}
				{dateField}
				{startTimeField}
				{endTimeField}
				<br />
				<Button variant="contained" color="primary" onClick={formSubmit}>
					Submit
				</Button>
			</form>
		</Container>
	);
}
